import React, { useEffect, useState } from "react";
import axios from "axios";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import image1 from "../../assets/images/homepage/hero-bg-1.jpg";
import LazyLoad from "react-lazy-load";
import Loading from "../../utils/loading";
const Banner = () => {
  const [mainbannerdata, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
    getSampleApiData();
  }, []);
  async function getSampleApiData() {
    try {
      const response = await axios(`https://admin.lining.com.np/api/home`);
      setData(response.data.headbanners);
      console.error("No Error fetching data: fds");
    } catch (error) {
      console.error("Error fetching data: ", error);
      setError(error);
    }
    setLoading(false);
  }
  if (loading) return <Loading />;
  if (error) return "Error!";
  return (
    <>
      <Carousel>
        {mainbannerdata.map((headbanners) => {
          return (
            <div key={headbanners.id}>
              <LazyLoad>
                <img src={headbanners.DocPath ? headbanners.DocPath : image1} />
              </LazyLoad>
            </div>
          );
        })}
      </Carousel>
    </>
  );
};
export default Banner;
