import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import img1 from "../../assets/images/products/wade-10-lavender-main.jpg";
const HotsleProductList = () => {
  const [hotsaleproductData, setData] = useState([]);
  useEffect(() => {
    axios
      .get(`https://admin.lining.com.np/api/Kids/${hotsaleproductData}`)
      .then((response) => {
        console.log("kids", response);
        setData(response.data.items);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);

  return (
    <>
      <section className="product-sec">
        <div className="container">
          <div className="row">
            <div className="col-12 col-sm-9 col-lg-10 items new-arrivals">
              <div className="new-items row">
                <div className="row row-cols-1 row-cols-sm-2 row-cols-lg-3 g-3">
                  {hotsaleproductData.length == 0
                    ? "No Data Found."
                    : hotsaleproductData.map((items) => {
                        return (
                          <div className="col" key={items.id}>
                            <a href={`/products/details/${items.id}`}>
                              <div className="card">
                                <img
                                  src={items.DocPath ? items.DocPath : img1}
                                  className="card-img-top"
                                />
                                <div className="card-body">
                                  <h6 className="card-title">
                                    {items.item_name}
                                  </h6>
                                  <p className="card-text">
                                    {items.category_user}
                                  </p>
                                  <p className="card-price">{items.color}</p>
                                  <h6>MRP: Rs {items.price}</h6>
                                </div>
                              </div>
                            </a>
                          </div>
                        );
                      })}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default HotsleProductList;
