import React, { useEffect, useState } from "react";

import axios from "axios";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import image1 from "../../assets/images/homepage/hero-bg-1.jpg";
import image2 from "../../assets/images/homepage/hero-bg-2.jpg";
import image3 from "../../assets/images/homepage/hero-bg-3.jpg";

const Banner = () => {
  const [mainbannerdata, setData] = useState([]);
  useEffect(() => {
    axios
      .get("https://admin.lining.com.np/api/home")
      .then((response) => {
        console.log("res", response);
        setData(response.data.headbanners);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);

  return (
    <>
      <Carousel>
        {mainbannerdata.map((headbanners) => {
          return (
            <div key={headbanners.id}>
              <img src={headbanners.DocPath} />
            </div>
          );
        })}
        {/* <div>
          <img src={image1} />
        </div>
        <div>
          <img src={image2} />
        </div>
        <div>
          <img src={image3} />
        </div> */}
      </Carousel>
    </>
  );
};
export default Banner;
