import React, { useEffect, useState } from "react";
import axios from "axios";
import img1 from "../../assets/images/main-logo.jpg";
import {
  BrowserRouter as Router,
  Route,
  useParams,
  Link,
  useNavigate,
} from "react-router-dom";
const Homepage4 = () => {
  const [Homepage4Data, setData] = useState([]);
  const navigate = useNavigate();
  useEffect(() => {
    axios
      .get(`https://admin.lining.com.np/api/home/${Homepage4Data}`)
      .then((response) => {
        console.log("resss", response);
        setData(response.data.main_categories);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);
  return (
    <>
      <section className="desc-banner-1">
        <div className="container">
          <h3>FLIGHT ESSENTIALS </h3>
          <p>
            Your built-to-last, all-week-wears---but with style only Jordan
            Brand can deliver.
          </p>
          <div className="btn-grp">
            <button className="btn-1">Shop</button>
          </div>
        </div>
      </section>
      <section className="essentials">
        <div className="container">
          <h3>The Essentials</h3>
          <div className="row row-cols-3 g-4">
            {Homepage4Data.length == 0
              ? "No Data Found."
              : Homepage4Data.map((main_categories) => {
                  return (
                    <div key={main_categories.id} className="col">
                      <div
                        className="card"
                        onClick={() =>
                          navigate(`/products/list/${main_categories.id}`)
                        }
                      >
                        <div className="catsss-img">
                          <img
                            src={
                              main_categories.DocPath
                                ? main_categories.DocPath
                                : img1
                            }
                          />
                        </div>
                        <div className="card-body">
                          <button className="btn-1">
                            {main_categories.category_name}
                          </button>
                        </div>
                      </div>
                    </div>
                  );
                })}
          </div>
        </div>
      </section>
    </>
  );
};
export default Homepage4;
