import React, { useEffect, useState } from "react";
import axios from "axios";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import image1 from "../../assets/images/homepage/hero-bg-1.jpg";
import LazyLoad from "react-lazy-load";
const Banner = () => {
  const [mainbannerdata, setData] = useState([]);
  useEffect(() => {
    axios
      .get(`https://admin.lining.com.np/api/home/${mainbannerdata}`)
      .then((response) => {
        console.log("res", response);
        setData(response.data.headbanners);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);

  return (
    <>
      <Carousel>
        {mainbannerdata.map((headbanners) => {
          return (
            <div key={headbanners.id}>
              <LazyLoad>
                <img src={headbanners.DocPath ? headbanners.DocPath : image1} />
              </LazyLoad>
            </div>
          );
        })}
      </Carousel>
    </>
  );
};
export default Banner;
