import React, { useEffect, useState } from "react";
import axios from "axios";
import img8 from "../../assets/images/homepage/hero-sec-bg-2.webp";
const BannerImage = () => {
  const [Homepage2Data, setData] = useState([]);
  useEffect(() => {
    axios
      .get("https://admin.lining.com.np/api/home")
      .then((response) => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
        console.log("res", response);
        setData(response.data.bodybanners);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);
  return (
    <>
      <section className="hero-section-2">
        <div className="container BannerImage">
          {Homepage2Data.map((bodybanners) => {
            return (
              <div key={bodybanners.id}>
                <img src={bodybanners.DocPath ? bodybanners.DocPath : img8} />
              </div>
            );
          })}
        </div>
      </section>
    </>
  );
};
export default BannerImage;
