import { useNavigate } from "react-router-dom";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import image1 from "../../assets/images/homepage/hero-bg-1.jpg";
import img1 from "../../assets/images/main-logo.jpg";
import img8 from "../../assets/images/homepage/hero-sec-bg-2.webp";
import Loading from "../../utils/loading";
const HomePage = () => {
  const navigate = useNavigate();
  const [mainbannerdata, setMainBannerData] = useState(null);
  const [Homepage1Data, setData] = useState([]);
  const [Homepage2Data, setData1] = useState([]);
  const [BannerData, setBannerData] = useState([]);
  const [MenItems, setMenItemData] = useState([]);
  const [WomenItems, setWmenItemData] = useState([]);
  const [Homepage4Data, setHomepage4Data] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
    getApiData();
  }, []);
  async function getApiData() {
    try {
      const response = await axios(`https://admin.lining.com.np/api/home`);
      setMainBannerData(response.data.headbanners);
      setData(response.data.categories);
      setData1(response.data.items);
      setBannerData(response.data.bodybanners);
      setMenItemData(response.data.men_items);
      setWmenItemData(response.data.women_items);
      setHomepage4Data(response.data.main_categories);
    } catch (error) {
      setError(error);
    }
    setLoading(false);
  }

  let settings1 = {
    dots: false,
    nav: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
  };
  let settings2 = {
    dots: false,
    nav: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
  };
  if (loading) return <Loading />;
  if (error) return "Error!";
  return (
    <>
      <LazyLoad>
        <Carousel>
          {mainbannerdata.map((headbanners) => {
            return (
              <div key={headbanners.id}>
                <img src={headbanners.DocPath ? headbanners.DocPath : image1} />
              </div>
            );
          })}
        </Carousel>
      </LazyLoad>
      <section className="desc-banner-1">
        <div className="container">
          <p>First Look</p>
          <h3>LI-NING AIR MAX PULSE</h3>
          <p>
            Extreme comfort. Hyper durable. Max volume. Introducing the Air Max
            Pulse ---designed to push you past your limits and help you go to
            the max.
          </p>
          <div className="btn-grp">
            <button className="btn-1">Notify Me</button>
            <button className="btn-1">Shop Air Max</button>
          </div>
        </div>
      </section>

      <section className="category">
        <div className="container">
          <div className="row row-cols-2 row-cols-md-4 g-4">
            {Homepage1Data.length == 0
              ? ""
              : Homepage1Data.map((categories) => {
                  return (
                    <div className="col" key={categories.id}>
                      <div
                        className="card h-100"
                        onClick={() =>
                          navigate(`/products/list/${categories.id}`)
                        }
                      >
                        <div className="catImagess">
                          <LazyLoad>
                            <img
                              src={
                                categories.DocPath ? categories.DocPath : img1
                              }
                              className="card-img-top"
                              alt="..."
                            />
                          </LazyLoad>
                        </div>
                        <div className="card-body catTitle">
                          <h5 className="card-title">
                            {categories.category_name}
                          </h5>
                        </div>
                      </div>
                    </div>
                  );
                })}
          </div>
        </div>
      </section>

      <section className="main-carousel">
        <div className="container">
          <h3>Best of Li-Ning</h3>
          <Slider {...settings1}>
            {Homepage2Data.map((items) => {
              return (
                <div key={items.id}>
                  <div
                    className="card"
                    onClick={() => navigate(`/products/details/${items.id}`)}
                  >
                    <div className="itemss-img">
                      <LazyLoad>
                        <img
                          src={items.DocPath ? items.DocPath : img1}
                          className="card-img-top"
                          alt="..."
                        />
                      </LazyLoad>
                    </div>
                    <div className="card-body">
                      <h6>{items.item_name}</h6>
                      <div className="d-flex justify-content-between">
                        <p>{items.category_user}</p>
                        <h6>Rs. {items.price}</h6>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </Slider>
        </div>
      </section>

      <section className="hero-section-2">
        <div className="container BannerImage">
          {BannerData.length == 0
            ? ""
            : BannerData.map((bodybanners) => {
                return (
                  <div key={bodybanners.id}>
                    <LazyLoad>
                      <img
                        src={bodybanners.DocPath ? bodybanners.DocPath : img8}
                      />
                    </LazyLoad>
                  </div>
                );
              })}
        </div>
      </section>

      <section className="desc-banner-1">
        <div className="container">
          <h3>STEP INTO WHAT FEELS GOOD</h3>
          <p>
            Cause everyone should know the feeling of running in that perfect
            pair.
          </p>
          <div className="btn-grp">
            <button className="btn-1">Find Your Shoe</button>
          </div>
        </div>
      </section>

      <section className="carousel-grp">
        <div className="container">
          <div className="row">
            <div className="col-6">
              <h4>Shop Men's</h4>
              <Slider {...settings2}>
                {MenItems.map((men_items) => {
                  return (
                    <div key={men_items.id}>
                      <div
                        className="card"
                        onClick={() =>
                          navigate(`/products/details/${men_items.id}`)
                        }
                      >
                        <div className="itemss-img">
                          <LazyLoad>
                            <img
                              src={men_items.DocPath ? men_items.DocPath : img1}
                              className="card-img-top"
                              alt="..."
                            />
                          </LazyLoad>
                        </div>
                        <div className="card-body">
                          <h6>{men_items.item_name}</h6>
                          <div className="d-flex justify-content-between">
                            <p>{men_items.category_user}</p>
                            <h6>Rs {men_items.price}</h6>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </Slider>
            </div>
            <div className="col-6">
              <h4>Shop Women's</h4>
              <Slider {...settings2}>
                {WomenItems.map((women_items) => {
                  return (
                    <div key={women_items.id}>
                      <div
                        className="card"
                        onClick={() =>
                          navigate(`/products/details/${women_items.id}`)
                        }
                      >
                        <div className="itemss-img">
                          <LazyLoad>
                            <img
                              src={
                                women_items.DocPath ? women_items.DocPath : img1
                              }
                              className="card-img-top"
                              alt="..."
                            />
                          </LazyLoad>
                        </div>
                        <div className="card-body">
                          <h6>{women_items.item_name}</h6>
                          <div className="d-flex justify-content-between">
                            <p>{women_items.category_user}</p>
                            <h6>Rs {women_items.price}</h6>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </Slider>
            </div>
          </div>
        </div>
      </section>

      <section className="desc-banner-1">
        <div className="container">
          <h3>FLIGHT ESSENTIALS </h3>
          <p>
            Your built-to-last, all-week-wears---but with style only Jordan
            Brand can deliver.
          </p>
          <div className="btn-grp">
            <button className="btn-1">Shop</button>
          </div>
        </div>
      </section>
      <section className="essentials">
        <div className="container">
          <h3>The Essentials</h3>
          <div className="row row-cols-3 g-4">
            {Homepage4Data.length == 0
              ? ""
              : Homepage4Data.map((main_categories) => {
                  return (
                    <div key={main_categories.id} className="col">
                      <div
                        className="card"
                        onClick={() =>
                          navigate(`/products/list/${main_categories.id}`)
                        }
                      >
                        <div className="catsss-img">
                          <LazyLoad>
                            <img
                              src={
                                main_categories.DocPath
                                  ? main_categories.DocPath
                                  : img1
                              }
                            />
                          </LazyLoad>
                        </div>
                        <div className="card-body">
                          <button className="btn-1">
                            {main_categories.category_name}
                          </button>
                        </div>
                      </div>
                    </div>
                  );
                })}
          </div>
        </div>
      </section>
    </>
  );
};
export default HomePage;
