import React, { useEffect, useState } from "react";
import axios from "axios";
import Slider from "react-slick";
import LazyLoad from "react-lazy-load";
import { Link, useNavigate } from "react-router-dom";
import img1 from "../../assets/images/main-logo.jpg";
import img8 from "../../assets/images/homepage/hero-sec-bg-2.webp";
const Homepage2 = () => {
  const navigate = useNavigate();
  const [Homepage2Data, setData] = useState([]);
  const [BannerData, setBannerData] = useState([]);
  useEffect(() => {
    axios
      .get(`https://admin.lining.com.np/api/home/${Homepage2Data}`)
      .then((response) => {
        console.log("res", response);
        setData(response.data.items);
        setBannerData(response.data.bodybanners);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);
  let settings = {
    dots: false,
    nav: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
  };
  return (
    <>
      <section className="main-carousel">
        <div className="container">
          <h3>Best of Li-Ning</h3>
          <Slider {...settings}>
            {Homepage2Data.map((items) => {
              return (
                <div key={items.id}>
                  <div
                    className="card"
                    onClick={() => navigate(`/products/details/${items.id}`)}
                  >
                    <div className="itemss-img">
                      <img
                        src={items.DocPath ? items.DocPath : img1}
                        className="card-img-top"
                        alt="..."
                      />
                    </div>
                    <div className="card-body">
                      <h6>{items.item_name}</h6>
                      <div className="d-flex justify-content-between">
                        <p>{items.category_user}</p>
                        <h6>Rs. {items.price}</h6>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </Slider>
        </div>
      </section>

      <section className="hero-section-2">
        <div className="container BannerImage">
          {BannerData.length == 0
            ? "No Data Found."
            : BannerData.map((bodybanners) => {
                return (
                  <div key={bodybanners.id}>
                    <img
                      src={bodybanners.DocPath ? bodybanners.DocPath : img8}
                    />
                  </div>
                );
              })}
        </div>
      </section>
    </>
  );
};
export default Homepage2;
