import React, { Component } from "react";
import ReactDOM from "react-dom";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import image1 from "../../assets/images/homepage/hero-bg-1.jpg";
import image2 from "../../assets/images/homepage/hero-bg-2.jpg";
import image3 from "../../assets/images/homepage/hero-bg-3.jpg";

const Banner = () => {
  const [Homepage1Data, setData] = useState([]);
  useEffect(() => {
    axios
      .get("https://admin.lining.com.np/api/home")
      .then((response) => {
        console.log("res", response);
        setData(response.data.banners);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);

  return (
    <>
      <Carousel>
        <div>
          <img src={image1} />
        </div>
        <div>
          <img src={image2} />
        </div>
        <div>
          <img src={image3} />
        </div>
      </Carousel>
    </>
  );
};
export default Banner;
