    try {
      const response = await axios(`https://admin.lining.com.np/api/home`);
      setData(response.data);
      console.log(response);
    } catch (error) {
      setError(error);
    }
    setLoading(false);
  }

  let settings1 = {
    dots: false,
    nav: true,
    infinite: true,
    speed: 500,
    // slidesToShow: 4,
    slidesToShow: slideData > 0 ? slideData : 3,
    slidesToScroll: 1,
  };
