    dots: false,
    nav: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
  };
  if (loading) return <Loading />;
  if (error) return "Error!";
  return (
    <>
      <LazyLoad>
        <Carousel>
          {Data.headbanners.map((i) => {
            return (
              <div key={i.id}>
                <img src={i.DocPath ? i.DocPath : image1} />
