      <section className="carousel-grp">
        <div className="container">
          <div className="row">
            <div className="col-6">
              <h4>Shop Men's</h4>
              <Slider {...settings2}>
                {Data.men_items.map((i) => {
                  return (
                    <div key={i.id}>
                      <div
                        className="card"
