                        <div className="itemss-img">
                          <LazyLoad>
                            <img
                              src={i.DocPath ? i.DocPath : img1}
                              className="card-img-top"
                              alt="..."
                            />
                          </LazyLoad>
                        </div>
                        <div className="card-body">
                          <h6>{i.item_name}</h6>
                          <div className="d-flex justify-content-between">
                            <p>{i.category_user}</p>
                            <h6>Rs {i.price}</h6>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </Slider>
            </div>
            <div className="col-6">
              <h4>Shop Women's</h4>
              <Slider {...settings2}>
                {Data.women_items.map((i) => {
                  return (
                    <div key={i.id}>
                      <div
                        className="card"
                        onClick={() => navigate(`/products/details/${i.id}`)}
                      >
                        <div className="itemss-img">
                          <LazyLoad>
                            <img
                              src={i.DocPath ? i.DocPath : img1}
                              className="card-img-top"
                              alt="..."
                            />
                          </LazyLoad>
                        </div>
                        <div className="card-body">
                          <h6>{i.item_name}</h6>
                          <div className="d-flex justify-content-between">
                            <p>{i.category_user}</p>
                            <h6>Rs {i.price}</h6>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </Slider>
            </div>
          </div>
        </div>
      </section>

      <section className="desc-banner-1">
        <div className="container">
          <h3>FLIGHT ESSENTIALS </h3>
          <p>
