import React from "react";
import Slider from "react-slick";
import img1 from "../../assets/images/homepage/fashionshoes.jpg";
const Homepage3 = () => {
  const [MenItems, setMenItemData] = useState([]);
  const [WomenItems, setWmenItemData] = useState([]);
  useEffect(() => {
    axios
      .get("https://admin.lining.com.np/api/home")
      .then((response) => {
        console.log("res", response);
        setMenItemData(response.data.men_items);
        setWmenItemData(response.data.women_items);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);
  let settings = {
    dots: true,
    nav: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
  };
  return (
    <>
      <section className="desc-banner-1">
        <div className="container">
          <h3>STEP INTO WHAT FEELS GOOD</h3>
          <p>
            Cause everyone should know the feeling of running in that perfect
            pair.
          </p>
          <div className="btn-grp">
            <button className="btn-1">Find Your Shoe</button>
          </div>
        </div>
      </section>

      <section className="carousel-grp">
        <div className="container">
          <div className="row">
            <div className="col-6">
              <h4>Shop Men's</h4>
              <Slider {...settings}>
                {MenItems.map((men_items) => {
                  return (
                    <div>
                      <div className="card" key={men_items.id}>
                        <div className="itemss-img">
                          <img
                            src={men_items.DocPath ? men_items.DocPath : img1}
                            className="card-img-top"
                            alt="..."
                          />
                        </div>
                        <div className="card-body">
                          <h6>Li-Ning Shoes</h6>
                          <div className="d-flex justify-content-between">
                            <p>Men's Shoes</p>
                            <h6>Rs 5,000</h6>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </Slider>
            </div>
            <div className="col-6">
              <h4>Shop Women's</h4>
              <Slider {...settings}>
                <div>
                  <div className="card">
                    <img src={img1} className="card-img-top" alt="..." />
                    <div className="card-body">
                      <div className="d-flex justify-content-between">
                        <h6>Li-Ning Shoes</h6>
                        <h6>Rs 5,000</h6>
                      </div>
                      <p>Men's Shoes</p>
                    </div>
                  </div>
                </div>
              </Slider>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default Homepage3;
