import React, { useEffect, useState } from "react";
import axios from "axios";
import LazyLoad from "react-lazy-load";
import {
  BrowserRouter as Router,
  Route,
  useParams,
  Link,
  useNavigate,
} from "react-router-dom";
import img1 from "../../assets/images/main-logo.jpg";
const Homepage1 = () => {
  const navigate = useNavigate();
  const [Homepage1Data, setData] = useState([]);
  useEffect(() => {
    axios
      .get(`https://admin.lining.com.np/api/home/${Homepage1Data}`)
      .then((response) => {
        // debugger;
        console.log("res", response);
        setData(response.data.categories);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);
  return (
    <>
      <section className="desc-banner-1">
        <div className="container">
          <p>First Look</p>
          <h3>LI-NING AIR MAX PULSE</h3>
          <p>
            Extreme comfort. Hyper durable. Max volume. Introducing the Air Max
            Pulse ---designed to push you past your limits and help you go to
            the max.
          </p>
          <div className="btn-grp">
            <button className="btn-1">Notify Me</button>
            <button className="btn-1">Shop Air Max</button>
          </div>
        </div>
      </section>

      <section className="category">
        <div className="container">
          <div className="row row-cols-2 row-cols-md-4 g-4">
            {Homepage1Data.length == 0
              ? "No Data Found."
              : Homepage1Data.map((categories) => {
                  return (
                    <div className="col" key={categories.id}>
                      <div
                        className="card h-100"
                        onClick={() =>
                          navigate(`/products/list/${categories.id}`)
                        }
                      >
                        <div className="catImagess">
                          <LazyLoad>
                            <img
                              src={
                                categories.DocPath ? categories.DocPath : img1
                              }
                              className="card-img-top"
                              alt="..."
                            />
                          </LazyLoad>
                        </div>
                        <div className="card-body catTitle">
                          <h5 className="card-title">
                            {categories.category_name}
                          </h5>
                        </div>
                      </div>
                    </div>
                  );
                })}
          </div>
        </div>
      </section>
    </>
  );
};
export default Homepage1;
