import React, { useEffect, useState } from "react";
import axios from "axios";
import Slider from "react-slick";
import { Link, useNavigate } from "react-router-dom";
import img1 from "../../assets/images/homepage/fashionshoes.jpg";
const Homepage3 = () => {
  const navigate = useNavigate();
  const [MenItems, setMenItemData] = useState([]);
  const [WomenItems, setWmenItemData] = useState([]);
  useEffect(() => {
    axios
      .get(`https://admin.lining.com.np/api/home/${MenItems}`)
      .then((response) => {
        console.log("res", response);
        setMenItemData(response.data.men_items);
        setWmenItemData(response.data.women_items);
      })
      .catch((e) => {
        console.log("e", e);
      });
  }, []);
  let settings = {
    dots: false,
    nav: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
  };
  return (
    <>
      <section className="desc-banner-1">
        <div className="container">
          <h3>STEP INTO WHAT FEELS GOOD</h3>
          <p>
            Cause everyone should know the feeling of running in that perfect
            pair.
          </p>
          <div className="btn-grp">
            <button className="btn-1">Find Your Shoe</button>
          </div>
        </div>
      </section>

      <section className="carousel-grp">
        <div className="container">
          <div className="row">
            <div className="col-6">
              <h4>Shop Men's</h4>
              <Slider {...settings}>
                {MenItems.map((men_items) => {
                  return (
                    <div key={men_items.id}>
                      <div
                        className="card"
                        onClick={() =>
                          navigate(`/products/details/${men_items.id}`)
                        }
                      >
                        <div className="itemss-img">
                          <img
                            src={men_items.DocPath ? men_items.DocPath : img1}
                            className="card-img-top"
                            alt="..."
                          />
                        </div>
                        <div className="card-body">
                          <h6>{men_items.item_name}</h6>
                          <div className="d-flex justify-content-between">
                            <p>{men_items.category_user}</p>
                            <h6>Rs {men_items.price}</h6>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </Slider>
            </div>
            <div className="col-6">
              <h4>Shop Women's</h4>
              <Slider {...settings}>
                {WomenItems.map((women_items) => {
                  return (
                    <div key={women_items.id}>
                      <div
                        className="card"
                        onClick={() =>
                          navigate(`/products/details/${women_items.id}`)
                        }
                      >
                        <div className="itemss-img">
                          <img
                            src={
                              women_items.DocPath ? women_items.DocPath : img1
                            }
                            className="card-img-top"
                            alt="..."
                          />
                        </div>
                        <div className="card-body">
                          <h6>{women_items.item_name}</h6>
                          <div className="d-flex justify-content-between">
                            <p>{women_items.category_user}</p>
                            <h6>Rs {women_items.price}</h6>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </Slider>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default Homepage3;
